import React, {useEffect, useState} from 'react';
import Item from "./Item";
import "./FashionPage.css";
import Hoodie from "../../Assets/ClothesImg/hoodie.jpg";
import Hat from "../../Assets/ClothesImg/hat.jpg";
import Jacket from "../../Assets/ClothesImg/jacket.jpg";
import LongSleeve from "../../Assets/ClothesImg/long-sleeve.jpg";
import Pants from "../../Assets/ClothesImg/pink-pants.jpg";
import Nike from "../../Assets/ClothesImg/nike-shoes.jpg";
import Jeans from "../../Assets/ClothesImg/jeans.jpg";
import Sneakers from "../../Assets/ClothesImg/sneaker-shoe.jpg";
import Header from "../Header";


const FashionPage = ({ handleSignIn, handleLogOut }) => {
    const [fashionItem, setFashionItem] = useState([]);
    const [itemToShow, setItemToShow] = useState(27);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const clothesApiUrl = "https://api.escuelajs.co/api/v1/products/?categoryId=1";
        const shoesApiUrl = "https://api.escuelajs.co/api/v1/products/?categoryId=4";

        const fetchFashionItems = async () => {
            try {
                const clothesResponse = await fetch(clothesApiUrl);
                const shoesResponse = await fetch(shoesApiUrl);

                if (!clothesResponse.ok || !shoesResponse.ok) {
                    throw new Error('Failed to fetch fashion items');
                }

                const clothesData = await clothesResponse.json();
                const shoesData = await shoesResponse.json();
                const combinedData = [...clothesData.slice(0,17), ...shoesData];

                setIsLoading(false);
                setFashionItem(combinedData);
                setItemToShow(12);
            } catch (error) {
                console.error('Error fetching fashion items:', error);
            }
        };

        fetchFashionItems();
    }, []);

    function handleMoreClick() {
        setItemToShow((prev) => prev + 10);
        const loadBtn = document.querySelector(".loadbtn");
        loadBtn.style.display = "none";
    }

    return (
        <>
            <Header handleSignIn={handleSignIn} handleLogOut={handleLogOut}/>
            <div className="fashion-page">
                <div className="filter-section">

                </div>
                <div className="fashion-form">
                    <h2 className="fashion-title">New Arrivals</h2>
                    <div className="item-list">
                        {isLoading ? (
                            <div className="spinner-border" role="status">
                                <span className="visually-hidden">Loading...</span>
                            </div>
                        ) : (
                            fashionItem.slice(0, itemToShow).map((item) => (
                                <Item
                                    key={item.id}
                                    id={item.id}
                                    images={item.images}
                                    title={item.title}
                                    price={item.price}
                                    description={item.description}
                                />
                            ))
                        )}
                    </div>
                    {fashionItem.length > itemToShow && (
                        <div className="loadbtn-container">
                            <button type="button" className="loadbtn btn btn-dark" onClick={handleMoreClick}>
                                Load More
                            </button>
                        </div>
                    )}
                </div>
            </div>
        </>
    );
};

export default FashionPage;
