import React, {useState} from 'react';
import {toast, ToastContainer} from "react-toastify";

const AddProductPage = () => {
    const categories = [
        { categoryId: 1, categoryName: 'Fashion & Clothes' },
        { categoryId: 2, categoryName: 'Electronics' },
        { categoryId: 3, categoryName: 'Home & Garden' },
        { categoryId: 4, categoryName: 'Miscellaneous' }
    ];

    const [formData, setFormData] = useState({
        productName: '',
        categoryId: '',
        price: '',
        stock_quantity: '',
        description: '',
        // orderDate: ''
        // image: null
    });

    function handleChange(e) {
        const { name, value, files } = e.target;
        console.log(`Changing ${name} to ${value}`);
        if (name === 'categoryName') {
            const selectedCategory = categories.find(category => category.categoryName === value);
            setFormData(prevState => ({
                ...prevState,
                categoryName: value,
                categoryId: selectedCategory ? selectedCategory.categoryId : ''
            }));
        // } else if (files) {
        //     console.log(`Found file: ${files[0].name}`);
        //     setFormData(prevState => ({
        //         ...prevState,
        //         [name]: files[0] // Store the file object
        //     }));
        } else {
            console.log(`Setting ${name} to ${value}`);
            setFormData(prevState => ({
                ...prevState,
                [name]: value
            }));
        }
    }

    function handleSubmit(e) {
        e.preventDefault();
        const formDataToSend = new FormData();
        for (const key in formData) {
            formDataToSend.append(key, formData[key]);
        }

        formDataToSend.append('orderDate', new Date().toISOString());

        fetch("http://localhost:8080/nexusHub/product/add", {
            method: "POST",
            body: formDataToSend // Use FormData object to send the file
        })
            .then((response) => {
            if (response.ok) {
                console.log('Product added Successfully');
                response.json().then(data => {
                    toast.success('Product added Successfully', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                })
            } else {
                console.error('Failed to save product');
                toast.warn('Failed to save product', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    }
                );
            }
        })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    return (
        <div>
            <h1>ADD PRODUCTS</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>
                        <h4>PRODUCT NAME</h4>
                        <input type="text" name="productName" value={formData.productName} onChange={handleChange}></input>
                    </label>
                </div>
                <div>
                    <label>
                        <h4>PRODUCT CATEGORY</h4>
                        <div className="btn-group">
                            <select className="btn btn-primary" name="categoryId" value={formData.categoryId} onChange={handleChange}>
                                <option value="">Select Category</option>
                                {categories.map(category => (
                                    <option key={category.categoryId} value={category.categoryId}>
                                        {category.categoryName}
                                    </option>
                                ))}
                            </select>
                        </div>
                    </label>
                </div>
                <div>
                    <label>
                        <h4>PRODUCT DESCRIPTION</h4>
                        <input type="text" name="description" value={formData.description} onChange={handleChange}></input>
                    </label>
                </div>
                <div>
                    <label>
                        <h4>AVAILABLE QUANTITY</h4>
                        <input type="text" name="stock_quantity" value={formData.stock_quantity} onChange={handleChange}></input>
                    </label>
                </div>
                <div>
                    <label>
                        <h4>PRICE</h4>
                        <input type="text" name="price" value={formData.price} onChange={handleChange}></input>
                    </label>
                </div>
                {/*<div>*/}
                {/*    <label>*/}
                {/*        <h4>IMAGE</h4>*/}
                {/*        <input type="file" name="image" onChange={handleChange}></input>*/}
                {/*    </label>*/}
                {/*</div>*/}
                <button type="submit">Save Product</button>
            </form>
            <ToastContainer />
        </div>
    );
};

export default AddProductPage;
