import React, {useState} from 'react';
import "./ChatBot.css";

export default function ChatBot() {
    const [isOpen, setIsOpen] = useState(true);

    function closeChat() {
        setIsOpen(!isOpen);
    }

    function openChat() {
        setIsOpen(true);
    }

    return(
        <div className={`chatbot ${isOpen ? 'open' : 'closed'}`}>
            {isOpen ? (
                <>
                    <button type="button" className="btn-close close-btn" aria-label="Close" onClick={closeChat}></button>
                    {/* Add your chatbot content here */}
                </>
            ) : (
                <button className="btn btn-primary chatbot-btn" onClick={openChat}>Need Help?</button>
            )}
        </div>
    )
}
