import React from 'react';

const CartItem = ({ cartItemId, productName, brand, images, price, quantity }) => {

    const data = {
        cart_item_id: cartItemId,
        productName: productName,
        brand: brand,
        price: price,
        images: images,
        quantity: quantity
    }

    return (
        <div className="card-container">
            <div className="card">
                {images && (
                    <img
                        src={images[0]}
                        className="card-img-top card-images"
                        alt={productName}
                    />
                )}
                <div className="card-body desc">
                    <p className="brand-txt">{brand}</p>
                    <h5 className="card-title">{productName}</h5>
                    <h4 className="price-txt">${price}</h4>
                    <div className="quantity-container">
                        <span className="quantity-txt">Qty:</span>
                        <select className="form-select" value={quantity}>
                            {[1,2,3,4,5].map((value) => (
                                <option key={value} value={value}>
                                    {value}
                                </option>
                            ))}
                        </select>
                    </div>
                    <button className="delete-btn">Delete</button>
                </div>
            </div>
        </div>
    );
};

export default CartItem;
