import React, {useState} from 'react';
import {toast} from "react-toastify";

const AddProductPage = () => {
    const categories = [
        {
            categoryId: 1,
            categoryName: "Fashion & Clothes"
        },
        {
            categoryId: 2,
            categoryName: "Electronics"
        },
        {
            categoryId: 3,
            categoryName: "Home & Garden"
        },
        {
            categoryId: 4,
            categoryName: "Miscellaneous"
        }
    ];

    const [formData, setFormData] = useState({
        productName: '',
        categoryName: '',
        price: '',
        quantity: '',
        description: '',
        image: ''
    });

    function handleChange(e) {
        const { name, value, files } = e.target;
        const category = categories.find(cat => cat.categoryName === value);
        if (files) {
            setFormData(prevState => ({
                ...prevState,
                [name]: files[0] // Store the file object
            }));
        } else if (category) {
            setFormData(prevState => ({
                ...prevState,
                [name]: category.categoryId
            }));
        } else {
            setFormData(prevState => ({
                ...prevState,
                [name]: value
            }));
        }
    }

    function handleCategorySelect(category) {
        setFormData(prevState => ({
            ...prevState,
            categoryName: category
        }));
    }
    function handleSubmit(e) {
        e.preventDefault();
        const formDataToSend = new FormData();
        for (const key in formData) {
            formDataToSend.append(key, formData[key]);
        }

        fetch("http://localhost:8080/nexusHub/cart/add", {
            method: "POST",
            body: formDataToSend // Use FormData object to send the file
        })
            .then((response) => {
            if (response.ok) {
                console.log('Product added Successfully');
                response.json().then(data => {
                    toast.success('Product added Successfully', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                })
            } else {
                console.error('Failed to save product');
                toast.warn('Failed to save product', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    }
                );
            }
        })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    return (
        <div>
            <h1>ADD PRODUCTS</h1>
            <form onSubmit={handleSubmit}>
                <div>
                    <label>
                        <h4>PRODUCT NAME</h4>
                        <input type="text" name="productName" value={formData.productName} onChange={handleChange}></input>
                    </label>
                </div>
                <div>
                    <label>
                        <h4>PRODUCT CATEGORY</h4>
                        <div className="btn-group">
                            <button className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                                {formData.categoryName || 'Category'}
                            </button>
                            <ul className="dropdown-menu">
                                <li className="dropdown-item" onClick={() => handleCategorySelect('Fashion & Clothes')}>Fashion & Clothes</li>
                                <li className="dropdown-item" onClick={() => handleCategorySelect('Homes & Garden')}>Homes & Garden</li>
                                <li className="dropdown-item" onClick={() => handleCategorySelect('Electronics')}>Electronics</li>
                                <li className="dropdown-item" onClick={() => handleCategorySelect('Sports')}>Sports</li>
                            </ul>
                        </div>
                    </label>
                </div>
                <div>
                    <label>
                        <h4>PRODUCT DESCRIPTION</h4>
                        <input type="text" name="description" value={formData.description} onChange={handleChange}></input>
                    </label>
                </div>
                <div>
                    <label>
                        <h4>AVAILABLE QUANTITY</h4>
                        <input type="text" name="quantity" value={formData.quantity} onChange={handleChange}></input>
                    </label>
                </div>
                <div>
                    <label>
                        <h4>PRICE</h4>
                        <input type="text" name="price" value={formData.price} onChange={handleChange}></input>
                    </label>
                </div>
                <div>
                    <label>
                        <h4>IMAGE</h4>
                        <input type="file" name="image" onChange={handleChange}></input>
                    </label>
                </div>
                <button type="submit">Save Product</button>
            </form>
        </div>
    );
};

export default AddProductPage;
