import React from 'react';
import {toast} from "react-toastify";

const AdminPage = () => {

    function addProduct(e) {
        e.preventDefault();
        const user = {username, password}
        console.log(user);
        fetch("http://localhost:8080/nexusHub/authenticate", {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(user)
        }).then((response) => {
            if (response.ok) {
                console.log('Login Successful');
                response.json().then(data => {
                    localStorage.setItem('token', data.token);
                    setIsLoggedIn(true);
                    // Save username to localStorage if "Remember Me" is checked
                    if (rememberMe) {
                        localStorage.setItem('rememberedUsername', username);
                    } else {
                        localStorage.removeItem('rememberedUsername');
                        setUsername('');
                    }
                    toast.success('Login Successful', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                })

                // Schedule token removal after 1 hour
                setTimeout(() => {
                    localStorage.removeItem('token');
                    setIsLoggedIn(false);
                    toast.warn('Session Expired. Please login again.', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                }, 60000 * 60);

                setPassword('');
            } else {
                console.error('Login Failed');
                toast.warn('Username or Password is incorrect', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    }
                );
            }
        })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    return (
        <div>
            <button className="btn btn-primary" onClick={addProduct}>Add new product</button>
            <table>
                <thead>
                    <tr>
                        <th>Name</th>
                        <th>Category</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Image</th>
                        <th>Update</th>
                        <th>Action</th>
                    </tr>
                </thead>
                <tbody>
                    <tr>
                        <td>{title}</td>
                        <td>{category}</td>
                        <td>{price}</td>
                        <td>{quantity}</td>
                        <td>{image}</td>
                        <td>{updateBtn}</td>
                        <td>{deleteBtn}</td>
                    </tr>
                </tbody>

                

            </table>
        </div>
    );
};

export default AdminPage;
