import React, {useState} from 'react';
import {toast, ToastContainer} from "react-toastify";
import "./AddProductPage.css";

const AddProductPage = () => {
    const categories = [
        { categoryId: 1, category_name: 'Fashion & Clothes' },
        { categoryId: 2, category_name: 'Electronics' },
        { categoryId: 3, category_name: 'Home & Garden' },
        { categoryId: 4, category_name: 'Miscellaneous' }
    ];

    const [formData, setFormData] = useState({
        productName: '',
        brand: '',
        categoryId: '',
        price: '',
        stock_quantity: '',
        description: '',
        imageUrl: []
    });

    const handleChange = (e) => {
        const { name, value, files } = e.target;
        if (name === 'imageUrl' && files.length > 0) {
            handleImageChange(files);
        } else {
            handleInputChange(name, value);
        }
    };

    const handleImageChange = (files) => {
        const imageFiles = Array.from(files);
        const imagePromises = imageFiles.map(file => {
            return new Promise((resolve, reject) => {
                const reader = new FileReader();
                reader.onload = (e) => {
                    resolve(e.target.result);
                };
                reader.onerror = reject;
                reader.readAsDataURL(file);
            });
        });

        Promise.all(imagePromises)
            .then(images => {
                setFormData(prevState => ({
                    ...prevState,
                    images: [...prevState.images, ...images]
                }));
                console.log('Uploaded file contents:', images);
            })
            .catch(error => console.error('Error reading files:', error));
    };

    function handleInputChange(name, value) {
        if (name === 'category_name') {
            const selectedCategory = categories.find(category => category.category_name === value);
            setFormData(prevState => ({
                ...prevState,
                category_name: value,
                categoryId: selectedCategory ? selectedCategory.categoryId : ''
            }));
        } else {
            console.log(`Setting ${name} to ${value}`);
            setFormData(prevState => ({
                ...prevState,
                [name]: value
            }));
        }
    }

    function handleSubmit(e) {
        e.preventDefault();
        const formDataToSend = new FormData();
        for (const key in formData) {
            if (Array.isArray(formData[key])) {
                formData[key].forEach(item => {
                    formDataToSend.append(key, item);
                });
            } else {
                formDataToSend.append(key, formData[key]);
            }
        }

        fetch("http://localhost:8080/nexusHub/product/add", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(formData)
        })
            .then((response) => {
            if (response.ok) {
                console.log('Product added Successfully');
                setFormData({
                    productName: '',
                    brand: '',
                    categoryId: '',
                    price: '',
                    stock_quantity: '',
                    description: '',
                    imageUrl: []
                });
                response.json().then(data => {

                    window.location.href = '/admin/product-control';

                    toast.success('Product added Successfully', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                })
            } else {
                console.error('Failed to save product');
                toast.warn('Failed to save product', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    }
                );
            }
        })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    return (
        <div className="add-product-page">
            <form className="add-product-form" onSubmit={handleSubmit}>
                <h1 className="add-product-title">ADD PRODUCTS</h1>
                <label>
                    <h4>PRODUCT NAME</h4>
                </label>
                <div className="input-group mb-3 input-box">
                    <textarea className="form-control" name="productName"  value={formData.productName} onChange={handleChange} required={true}></textarea>
                </div>
                <label>
                    <h4>BRAND NAME</h4>
                </label>
                <div className="input-group mb-3 input-box">
                    <textarea className="form-control" name="brand"  value={formData.brand} onChange={handleChange} required={true}></textarea>
                </div>
                <label>
                    <h4>PRODUCT CATEGORY</h4>
                </label>
                <div className="input-group mb-3 input-box">
                    <div className="btn-group">
                        <select className="form-select category-select" name="categoryId" value={formData.categoryId} onChange={handleChange} required={true}>
                            <option value="">Select Category</option>
                            {categories.map(category => (
                                <option key={category.categoryId} value={category.categoryId}>
                                    {category.category_name}
                                </option>
                            ))}
                        </select>
                    </div>
                </div>
                <label>
                    <h4 className="">PRODUCT DESCRIPTION</h4>
                </label>
                <div className="input-group mb-3 input-box">
                    <textarea className="form-control add-product-desc" name="description" value={formData.description} onChange={handleChange} required={true}></textarea>
                </div>
                <label>
                    <h4 className="">AVAILABLE QUANTITY</h4>
                </label>
                <div className="input-group mb-3 input-box">
                    <input type="text" className="form-control" name="stock_quantity" value={formData.stock_quantity} onChange={handleChange} required={true}></input>
                </div>
                <label>
                    <h4 className="">PRICE</h4>
                </label>
                <div className="input-group mb-3 input-box">
                        <span className="input-group-text price-icon">$</span>
                        <input type="text" className="form-control" name="price" value={formData.price} onChange={handleChange} required={true}></input>
                </div>
                <label>
                    <h4>IMAGE</h4>
                </label>
                <div className="input-group mb-3 input-box">
                    <input type="file" className="form-control" name="imageUrl" id="formFileSm" onChange={handleChange} multiple></input>
                </div>
                <button type="submit" className="btn btn-primary">Save Product</button>
            </form>
            <ToastContainer />
        </div>
    );
};

export default AddProductPage;
