import './Item.css';
import {useEffect, useState} from "react";


const Item = ({ id, title, images, description, price }) => {
        const [clothesDetail, setClothesDetail] = useState(null);

    useEffect(() => {
        const apiUrl = "https://api.escuelajs.co/api/v1/products"
        const fetchURL = async() =>{
            try {
                const res = await fetch(apiUrl);
                const data = await res.json();
                setClothesDetail(data);
                console.log(data);
            } catch (error) {
                console.error('Error fetching clothes details:', error);
            }
        }
        fetchURL();
    },[]);

    return (
        <div className="card-container">
            <div className="card">
                <img src={images} className="card-img-top card-images" alt={title} />
                <div className="card-body desc">
                    <p className="brand-txt">NexusHub</p>
                    <h5 className="card-title">{title}</h5>
                    <p className="product-desc">{description}</p>
                    <h4 className="price-txt">{price}</h4>
                    <button className="add-to-cart"><i className="bi bi-cart3 cart-icon" id={id}></i></button>
                </div>
            </div>
        </div>
    );
};

export default Item;
