import React, {useState} from 'react';
import {toast} from "react-toastify";

const AddProductPage = () => {
    const [formData, setFormData] = useState({
        productName: '',
        categoryId: '',
        price: '',
        quantity: '',
        description: '',
        image: ''
    });

    const handleChange = (e) => {
        const { name, value } = e.target;
        setFormData(prevState => ({
            ...prevState,
            [name]: value
        }));
    };
    function handleSubmit(e) {
        e.preventDefault();

        fetch("http://localhost:8080/nexusHub/cart/add", {
            method: "POST",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(product)
        })
            .then((response) => {
            if (response.ok) {
                console.log('Product added Successfully');
                response.json().then(data => {
                    toast.success('Product added Successfully', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                })
            } else {
                console.error('Failed to save product');
                toast.warn('Failed to save product', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    }
                );
            }
        })
            .catch((error) => {
                console.error('Error:', error);
            });
    };

    return (
        <div>
            <h1>ADD PRODUCTS</h1>
            <form onSubmit={handleSubmit}>
                <label>
                    <h4>PRODUCT NAME</h4>
                    <input type="text"></input>
                </label>
                <br/>
                <label>
                    <h4>PRODUCT CATEGORY</h4>
                    <div className="btn-group">
                        <button className="btn btn-secondary dropdown-toggle" type="button" data-bs-toggle="dropdown" aria-expanded="false">
                            Category
                        </button>
                        <ul className="dropdown-menu">
                            <li className="dropdown-item">Fashion & Clothes</li>
                            <li className="dropdown-item">Homes & Garden</li>
                            <li className="dropdown-item">Electronics</li>
                            <li className="dropdown-item">Sports</li>
                        </ul>
                    </div>
                </label>
                <br/>
                <label>
                    <h4>PRODUCT DESCRIPTION</h4>
                    <input type="text"></input>
                </label>
                <br/>
                <label>
                    <h4>AVAILABLE QUANTITY</h4>
                    <input type="text"></input>
                </label>
                <br/>
                <label>
                    <h4>PRICE</h4>
                    <input type="text"></input>
                </label>
                <br/>
                <label>
                    <h4>IMAGE</h4>
                    <input type="file"></input>
                </label>
            </form>
        </div>
    );
};

export default AddProductPage;
