import React, {useState} from 'react';
import "../AdminPage/ProductItem.css";
import {toast, ToastContainer} from "react-toastify";

const ProductItem = ({productId, productName, categoryName, categoryId, brand, description, price, stockQuantity, imageUrl, photos }) => {
    const [isEditing, setIsEditing] = useState(false);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);
    const [msg, setMsg] = useState(null);

    const displayImages = (photos && photos.length > 0) ? photos : imageUrl;
    const imagePath = process.env.PUBLIC_URL + ((photos && photos.length > 0) ? photos[currentImageIndex] : "");

    const categories = [
        { categoryId: 1, category_name: 'Fashion & Clothes' },
        { categoryId: 2, category_name: 'Electronics' },
        { categoryId: 3, category_name: 'Home & Garden' },
        { categoryId: 4, category_name: 'Miscellaneous' }
    ];

    const [formData, setFormData] = useState({
        productId: '',
        productName: '',
        brand:'',
        category_name: '',
        price: '',
        stock_quantity: '',
        description: '',
        imageUrl: [],
        photos: [],
        images: [],
        photoImagePath: photos && photos.length > 0 ? photos[0] : ""
    });

    const handleMouseEnter = () => {
        if (displayImages.length > 1) {
            const interval = setInterval(() => {
                setCurrentImageIndex(prevIndex =>
                    prevIndex < photos.length - 1 ? prevIndex + 1 : 0
                );
            }, 1000);

            setTimeout(() => {
                clearInterval(interval);
            }, 3000);
        }
    };

    const handleMouseLeave = () => {
        setCurrentImageIndex(0);
    };

    function handleChange(e) {
        const { name, value, files } = e.target;
        if (name === 'image' && files.length > 0) {
            handleImage(e);
        } else if (name === 'category_name') {
            handleCategoryChange(name, value);
        } else {
            setFormData(prevState => ({
                ...prevState,
                [name]: value
            }));
        }
    }

    function handleImage(files) {
        if (!files) {
            setMsg("No file selected");
            return;
        }
        
        const fileList = Array.from(files);
        console.log(fileList);
        setFormData(prevState => ({
            ...prevState,
            images: fileList
        }));
    }

    function handleCategoryChange(name, value) {
        const selectedCategory = categories.find(category => category.category_name === value);
        console.log("Change" + name + "To" + value)
        setFormData(prevState => ({
            ...prevState,
            [name]: selectedCategory ? selectedCategory.categoryId : ''
        }));
    }

    const toggleEditMode = () => {
        setIsEditing(!isEditing);
        setFormData({
            productName,
            categoryId,
            brand,
            description,
            price,
            stockQuantity,
            photos: [],
            image: null
        });
    };

    function handleUpdate(e) {
        e.preventDefault();
        const formDataToSend = new FormData();
        formDataToSend.append('productName', formData.productName);
        formDataToSend.append('brand', formData.brand);
        formDataToSend.append('categoryId', formData.categoryId);
        formDataToSend.append('price', formData.price);
        formDataToSend.append('stock_quantity', formData.stock_quantity);
        formDataToSend.append('description', formData.description);
        if (formData.image) {
            formDataToSend.append('image', formData.image);
        }

        fetch(`http://localhost:8080/nexusHub/product/update/${productId}`, {
            method: "PUT",
            // headers: {
            //     'Content-Type': 'multipart/form-data'
            // },
            body: formDataToSend
        })
            .then((response) => {
                if (response.ok) {
                    console.log('Product updated Successfully');

                    response.json().then(data => {
                        toast.success('Product updated Successfully', {
                            position: "top-center",
                            draggable: true,
                            hideProgressBar: true
                        });

                        setTimeout(() => {
                            window.location.reload();
                        }, 2000)

                    })
                } else {
                    console.error('Failed to update product');
                    toast.warn('Failed to update product', {
                            position: "top-center",
                            draggable: true,
                            hideProgressBar: true
                        }
                    );
                }
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    function handleDelete(e) {
        e.preventDefault();
        const deleteConfirmMessage = window.confirm("Are you sure you want to delete this product?");
        if (deleteConfirmMessage) {
            fetch(`http://localhost:8080/nexusHub/product/delete/${productId}`, {
                method: "DELETE"
            })
                .then((response) => {
                    if (response.ok) {
                        console.log('Product deleted Successfully');

                        toast.success('Product deleted Successfully', {
                            position: "top-center",
                            draggable: true,
                            hideProgressBar: true
                        });

                        setTimeout(() => {
                            window.location.reload();
                        }, 2000)

                    } else {
                        console.error('Failed to delete product');
                        toast.warn('Failed to delete product', {
                                position: "top-center",
                                draggable: true,
                                hideProgressBar: true
                            }
                        );
                    }
                })
                .catch((error) => {
                    console.error('Error:', error);
                });
        }
    }

    return (
        <>
            <tr>
                <td className="tr-col product-info-txt">{productId}</td>
                <td className="tr-col product-info-txt">
                    {isEditing ? (
                        <input
                            type="text"
                            className="form-control"
                            name="productName"
                            value={formData.productName}
                            onChange={handleChange}
                            multiple
                        />
                    ) : (
                        productName
                    )}
                </td>
                <td className="tr-col product-info-txt">
                    {isEditing ? (
                        <select
                            className="form-select"
                            name="category_name"
                            value={formData.categoryId}
                            onChange={handleChange}
                        >
                            {categories.map(category => (
                                <option key={category.category_name} value={category.categoryId}>
                                    {category.category_name}
                                </option>
                            ))}
                        </select>
                    ) : (
                        categoryName
                    )}
                </td>
                <td className="tr-col product-info-txt">
                    {isEditing ? (
                        <input
                            type="text"
                            className="form-control"
                            name="brand"
                            value={formData.brand}
                            onChange={handleChange}
                        />
                    ) : (
                        brand
                    )}
                </td>
                <td className="tr-col product-info-txt">
                    {isEditing ? (
                        <input
                            type="text"
                            className="form-control"
                            name="description"
                            value={formData.description}
                            onChange={handleChange}
                        />
                    ) : (
                        description
                    )}
                </td>
                <td className="tr-col product-info-txt">
                    {isEditing ? (
                        <input
                            type="text"
                            className="form-control"
                            name="price"
                            value={formData.price}
                            onChange={handleChange}
                        />
                    ) : (
                        price
                    )}
                </td>
                <td className="tr-col product-info-txt">
                    {isEditing ? (
                        <input
                            type="text"
                            className="form-control"
                            name="stockQuantity"
                            value={formData.stockQuantity}
                            onChange={handleChange}
                            accept="image/*"
                            multiple
                        />
                    ) : (
                        stockQuantity
                    )}
                </td>
                <td className="tr-col product-info-txt">
                    {isEditing ? (
                        <input
                            type="file"
                            className="form-control"
                            name="image"
                            onChange={handleChange}
                            accept="image/*"
                            multiple
                        />
                    ) : displayImages.length > 0 ? (
                        <img
                            className="product-info-img"
                            src={imagePath}
                            alt={productName}
                            onMouseEnter={handleMouseEnter}
                            onMouseLeave={handleMouseLeave}
                        />
                    ) : null}
                </td>
                <td className="tr-col product-info-txt">
                    {isEditing ? (
                        <button className="btn btn-primary" onClick={handleUpdate}>UPDATE</button>
                    ) : (
                        <button className="btn btn-primary" onClick={toggleEditMode}>EDIT</button>
                    )}
                </td>
                <td className="tr-col product-info-txt"><button className="btn btn-danger" onClick={handleDelete}>DELETE</button></td>
            </tr>
            <ToastContainer />
        </>
    );
};

export default ProductItem;
