import React from 'react';
import './CartItem.css';

const CartItem = ({ productName, brand, images, price, quantity }) => {

    return (
        <div className="cart-item-container">
            <div className="cart-item">
                {images && (
                    <img
                        src={images[0]}
                        className="card-images"
                        alt={productName}
                    />
                )}
                <div className="card-body cart-item-desc">
                    <p className="brand-txt">{brand}</p>
                    <h5 className="card-title">{productName}</h5>
                    <h4 className="price-txt">${price}</h4>
                    <div className="quantity-container">
                        <span className="quantity-txt">Qty:</span>
                        <select className="form-select" value={quantity} onChange={(e) => console.log(e.target.value)}>
                            {[1,2,3,4,5].map((value) => (
                                <option key={value} value={value}>
                                    {value}
                                </option>
                            ))}
                        </select>
                    </div>
                    <button className="delete-btn">Delete</button>
                </div>
            </div>
        </div>
    );
};

export default CartItem;
