import React, {useEffect, useState} from 'react';

const CartItem = ({ cart_item_id, productName, brand, images, price, quantity }) => {
    const [cartItems, setCartItems] = useState([]);
    const [isLoading, setIsLoading] = useState(true);
    
    const data = {
        cartItemId: cart_item_id,
        productName: productName,
        brand: brand,
        price: price,
        images: images,
        quantity: quantity
    }

    useEffect(() => {
        const fetchCartItems = async () => {
            try {
                const response = await fetch("http://localhost:8080/nexusHub/cart/c8894256-1bd4-4ba5-9abc-98ee48668569");
                if (response.ok) {
                    const data = await response.json();
                    setCartItems(data);
                    setIsLoading(false);
                } else {
                    console.error("Failed to fetch cart items");
                }
            } catch (error) {
                console.error("Error fetching cart items:", error);
            }
        };

        fetchCartItems();
    },[]);

    return (
        <div className="card-container">
            <div className="card">
                {images && (
                    <img
                        src={images[0]}
                        className="card-img-top card-images"
                        alt={productName}
                    />
                )}
                <div className="card-body desc">
                    <p className="brand-txt">{brand}</p>
                    <h5 className="card-title">{productName}</h5>
                    <h4 className="price-txt">${price}</h4>
                    <div className="quantity-container">
                        <span className="quantity-txt">Qty:</span>
                        <select className="form-select" value={quantity}>
                            {[1,2,3,4,5].map((value) => (
                                <option key={value} value={value}>
                                    {value}
                                </option>
                            ))}
                        </select>
                    </div>
                    <button className="delete-btn">Delete</button>
                </div>
            </div>
        </div>
    );
};

export default CartItem;
