import React, {useState} from 'react';
import {toast, ToastContainer} from "react-toastify";

const AdminLoginPage = () => {
    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);

    function handleSignIn(e) {
        e.preventDefault();
        const user = {username, password}
        console.log(user);
        fetch("http://localhost:8080/nexusHub/admin/login", {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(user)
        }).then((response) => {
            if (response.ok) {
                console.log('Login Successful');
                response.json().then(data => {
                    localStorage.setItem('token', data.token);
                    setIsLoggedIn(true);
                    // Save username to localStorage if "Remember Me" is checked
                    toast.success('Login Successful', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                })

                // Schedule token removal after 1 hour
                setTimeout(() => {
                    localStorage.removeItem('token');
                    setIsLoggedIn(false);
                    toast.warn('Session Expired. Please login again.', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                }, 60000 * 60);

                setPassword('');
            } else {
                console.error('Login Failed');
                toast.warn('Username or Password is incorrect', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    }
                );
            }
        })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    function handleLogOut() {
        // Clear token from localStorage upon logout
        localStorage.removeItem('token');

        setIsLoggedIn(false);
        toast.success("Logout Successful", {
                position: "top-center",
                draggable: true,
                hideProgressBar: true
            }
        )
    }

    return (
        <div>
            <form>
                <div>
                    <h1>Login</h1>
                    <input type="text" name="textName" placeholder="Name" autoFocus/>
                        <input type="password" name="textPwd" placeholder="Password"/>
                            <input type="submit" value="Login"/>
                                <p>Don't have an account? <a href="/Chatbot_Project/RegisterServlet">Signup</a></p>

                </div>
            </form>
            <ToastContainer />
        </div>
    );
};

export default AdminLoginPage;
