import React, {useState} from 'react';
import {toast, ToastContainer} from "react-toastify";

const User = ({userId, username, password, email, phoneNumber, created}) => {
    const [isAccordionOpen, setIsAccordionOpen] = useState(false);

    const [formData, setFormData] = useState({
        userId: '',
        username: '',
        password: '',
        email: '',
        phoneNumber: '',
        created: ''
    });

    const toggleAccordion = () => {
        setIsAccordionOpen(!isAccordionOpen);
    };

    function handleChange(e) {
        const { name, value } = e.target;
        console.log(`Changing ${name} to ${value}`);
        setFormData(prevState => ({
            ...prevState,
            [name]: value
            }));
    }


    function handleUpdate(e) {
        e.preventDefault();
        const formDataToSend = new FormData();
        for (const key in formData) {
            formDataToSend.append(key, formData[key]);
        }

        fetch(`http://localhost:8080/nexusHub/admin/update/${userId}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json" // Set the content type to JSON
            },
            body: JSON.stringify(formData)
        })
            .then((response) => {
                if (response.ok) {
                    console.log('User updated Successfully');
                    setFormData({
                        userId: '',
                        username: '',
                        password: '',
                        email: '',
                        phoneNumber: '',
                        created: ''
                    });
                    response.json().then(data => {
                        toast.success('User updated Successfully', {
                            position: "top-center",
                            draggable: true,
                            hideProgressBar: true
                        });
                        window.location.reload();
                    })
                } else {
                    console.error('Failed to update user');
                    toast.warn('Failed to update user', {
                            position: "top-center",
                            draggable: true,
                            hideProgressBar: true
                        }
                    );
                }
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

        function handleDelete(e) {
            e.preventDefault();

            fetch(`http://localhost:8080/nexusHub/product/delete/${productId}`, {
                method: "DELETE"
            })
                .then((response) => {
                    if (response.ok) {
                        console.log('Product deleted Successfully');

                        toast.success('Product deleted Successfully', {
                            position: "top-center",
                            draggable: true,
                            hideProgressBar: true
                        });

                        setTimeout(() => {
                            window.location.reload();
                        }, 2000)

                    } else {
                        console.error('Failed to delete product');
                        toast.warn('Failed to delete product', {
                                position: "top-center",
                                draggable: true,
                                hideProgressBar: true
                            }
                        );
                    }
                })
                .catch((error) => {
                    console.error('Error:', error);
                });
        }

    return (
        <>
            <tr>
                <td className="tr-col">{userId}</td>
                <td className="tr-col">{username}</td>
                <td className="tr-col">{password}</td>
                <td className="tr-col">{email}</td>
                <td className="tr-col">{phoneNumber}</td>
                <td className="tr-col">{created}</td>
                <td className="tr-col"><button className="btn btn-primary" onClick={toggleAccordion}>UPDATE</button></td>
                <td className="tr-col"><button className="btn btn-danger" onClick={handleDelete}>DELETE</button></td>
            </tr>
            {isAccordionOpen && (
                <tr>
                    <td colSpan="7">
                        <form className="add-product-form" onSubmit={handleUpdate}>
                            <h1>UPDATE USERS</h1>
                            <label>
                                <h4>USER NAME</h4>
                            </label>
                            <div className="input-group mb-3 input-box">
                                <input type="text" className="form-control" name="username"  value={formData.username} onChange={handleChange} required={true}></input>
                            </div>
                            <label>
                                <h4>USER PASSWORD</h4>
                            </label>
                            <div className="input-group mb-3 input-box">
                                <div className="input-group mb-3 input-box">
                                    <input type="text" className="form-control" name="password"  value={formData.password} onChange={handleChange} required={true}></input>
                                </div>
                            </div>
                            <label>
                                <h4 className="">EMAIL</h4>
                            </label>
                            <div className="input-group mb-3 input-box">
                                <input type="text" className="form-control" name="email" value={formData.email} onChange={handleChange} required={true}></input>
                            </div>
                            <label>
                                <h4 className="">PHONE NUMBER</h4>
                            </label>
                            <div className="input-group mb-3 input-box">
                                <input type="text" className="form-control" name="phoneNumber" value={formData.phoneNumber} onChange={handleChange} required={true}></input>
                            </div>
                            <label>
                                <h4 className="">CREATED</h4>
                            </label>
                            <div className="input-group mb-3 input-box">
                                <span className="input-group-text price-icon">$</span>
                                <input type="text" className="form-control" name="created" value={formData.created} onChange={handleChange} required={true}></input>
                            </div>
                            <button type="submit" className="btn btn-primary">Save User</button>
                        </form>
                    </td>
                </tr>
            )}
            <ToastContainer />
        </>
    );
};

export default User;
