import React, {useEffect, useState} from "react";
import Header from "../Header";
import "./ShoppingCart.css";
import ExampleImg from "../../Assets/ElectronicImg/camera.jpg";
import Item from "../CategoryPages/Item";
import CartItem from "./CartItem";
export default function ShoppingCart() {
    const [cartItems, setCartItems] = useState([]);
    const [isLoading, setIsLoading] = useState(true);



    useEffect(() => {
        const fetchCartItems = async () => {
            const cartItem = {productId, quantity}
            fetch("http://localhost:8080/nexusHub/cart/{user_id}", {
                method: "GET",
                headers: {"Content-Type": "application/json"},
                body: JSON.stringify(cartItem)
            }).then((response) => {
                if (response.ok) {
                    console.log('Cart data fetched Successful');
                } else {
                    console.error('Data fetch Failed');
                }
            })
        };

        fetchCartItems();
    },[]);


    return(
        <>
            <Header/>
            <div className="cart">
                <div className="cart-container">
                    <div className="cart-title-container">
                        <h3 className="cart-title">Your Items</h3>
                    </div>
                    <ul className="product-list">
                        <li className="product">
                            <div className="card shopping-card">
                                {isLoading ? (
                                    <div className="spinner-border" role="status">
                                        <span className="visually-hidden">Loading...</span>
                                    </div>
                                ) : (
                                    item && item.length > 0 && item.slice(0, itemToShow).map((item) => (
                                        <CartItem
                                            key={item.id}
                                            images={item.images}
                                            productName={item.productName}
                                            brand={item.brand}
                                            price={item.price}
                                            quantity={item.quantity}
                                        />
                                    ))
                                )}

                            </div>
                        </li>
                    </ul>


                </div>
            </div>

        </>
    )
}
