import React, {useEffect, useState} from 'react';
import "../AdminPage/ProductItem.css";
import {toast, ToastContainer} from "react-toastify";

const ProductItem = ({productId, productName, categoryName, brand, description, price, stockQuantity, imageUrl}) => {
    const [isAccordionOpen, setIsAccordionOpen] = useState(false);
    const [currentImageIndex, setCurrentImageIndex] = useState(0);

    const categories = [
        { categoryId: 1, category_name: 'Fashion & Clothes' },
        { categoryId: 2, category_name: 'Electronics' },
        { categoryId: 3, category_name: 'Home & Garden' },
        { categoryId: 4, category_name: 'Miscellaneous' }
    ];

    const [formData, setFormData] = useState({
        productId: '',
        productName: '',
        brand:'',
        categoryId: '',
        price: '',
        stock_quantity: '',
        description: '',
        imageUrl: []
    });

    const handleMouseEnter = () => {
        const interval = setInterval(() => {
            setCurrentImageIndex(prevIndex =>
                prevIndex < imageUrl.length - 1 ? prevIndex + 1 : 0
            );
        }, 1000);

        setTimeout(() => {
            clearInterval(interval);
        }, 3000);
    };

    const handleMouseLeave = () => {
        setCurrentImageIndex(0);
    };

    const toggleAccordion = () => {
        setIsAccordionOpen(!isAccordionOpen);
    };

    function productChange(e) {
        const { name, value, files } = e.target;
        console.log(`Changing ${name} to ${value}`);
        if (name === 'images' && files.length > 0) {
            const file = files[0]; // Get the first file from the files array
            const fileReader = new FileReader();

            fileReader.onload = (e) => {
                const fileContent = e.target.result;
                setFormData(prevState => ({
                    ...prevState,
                    [name]: [`"` + fileContent + `"`] // Store the file content in an array
                }));
            };

            fileReader.readAsDataURL(file); // Read the file as a data URL
        } else {
            if (name === 'category_name') {
                const selectedCategory = categories.find(category => category.category_name === value);
                setFormData(prevState => ({
                    ...prevState,
                    category_name: value,
                    categoryId: selectedCategory ? selectedCategory.categoryId : ''
                }));
            } else {
                console.log(`Setting ${name} to ${value}`);
                setFormData(prevState => ({
                    ...prevState,
                    [name]: value
                }));
            }
        }
    }

    function handleUpdate(e) {
        e.preventDefault();
        const formDataToSend = new FormData();
        for (const key in formData) {
            formDataToSend.append(key, formData[key]);
        }

        fetch(`http://localhost:8080/nexusHub/product/update/${productId}`, {
            method: "PUT",
            headers: {
                "Content-Type": "application/json"
            },
            body: JSON.stringify(formData)
        })
            .then((response) => {
                if (response.ok) {
                    console.log('Product updated Successfully');
                    setFormData({
                        productId: '',
                        product: '',
                        brandName: '',
                        categoryId: '',
                        price: '',
                        stock_quantity: '',
                        description: '',
                        imageUrl: ''
                    });

                    response.json().then(data => {
                        toast.success('Product updated Successfully', {
                            position: "top-center",
                            draggable: true,
                            hideProgressBar: true
                        });

                        setTimeout(() => {
                            window.location.reload();
                        }, 2000)

                    })
                } else {
                    console.error('Failed to update product');
                    toast.warn('Failed to update product', {
                            position: "top-center",
                            draggable: true,
                            hideProgressBar: true
                        }
                    );
                }
            })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    function handleDelete(e) {
        e.preventDefault();
        if (window.confirm("Are you sure you want to delete this product?")) {
            fetch(`http://localhost:8080/nexusHub/product/delete/${productId}`, {
                method: "DELETE"
            })
                .then((response) => {
                    if (response.ok) {
                        console.log('Product deleted Successfully');

                        toast.success('Product deleted Successfully', {
                            position: "top-center",
                            draggable: true,
                            hideProgressBar: true
                        });

                        setTimeout(() => {
                            window.location.reload();
                        }, 2000)

                    } else {
                        console.error('Failed to delete product');
                        toast.warn('Failed to delete product', {
                                position: "top-center",
                                draggable: true,
                                hideProgressBar: true
                            }
                        );
                    }
                })
                .catch((error) => {
                    console.error('Error:', error);
                });
        }
    }

    return (
        <>
            <tr>
                <td className="tr-col product-info-txt">{productId}</td>
                <td className="tr-col product-info-txt">{productName}</td>
                <td className="tr-col product-info-txt">{categoryName}</td>
                <td className="tr-col product-info-txt">{brand}</td>
                <td className="tr-col product-info-txt">{description}</td>
                <td className="tr-col product-info-txt">{price}</td>
                <td className="tr-col product-info-txt">{stockQuantity}</td>
                <td className="tr-col product-info-txt">
                    {imageUrl && (
                        <img
                            className="product-info-img"
                            src={imageUrl[currentImageIndex]}
                            alt="images"
                            onMouseEnter={handleMouseEnter}
                            onMouseLeave={handleMouseLeave}
                        />
                    )}
                </td>
                <td className="tr-col product-info-txt"><button className="btn btn-primary" onClick={toggleAccordion}>UPDATE</button></td>
                <td className="tr-col product-info-txt"><button className="btn btn-danger" onClick={handleDelete}>DELETE</button></td>
            </tr>
            {isAccordionOpen && (
                <tr>
                    <td colSpan="7">
                    <form className="add-product-form" onSubmit={handleUpdate}>
                        <h1>UPDATE PRODUCTS</h1>
                        <label>
                            <h4>PRODUCT NAME</h4>
                        </label>
                        <div className="input-group mb-3 input-box">
                            <input type="text" className="form-control" name="productName"  value={formData.productName} onChange={productChange} required={true}></input>
                        </div>
                        <label>
                            <h4>PRODUCT CATEGORY</h4>
                        </label>
                        <div className="input-group mb-3 input-box">
                            <div className="btn-group">
                                <select className="form-select category-select" name="categoryId" value={formData.categoryId} onChange={productChange} required={true}>
                                    <option value="">Select Category</option>
                                    {categories.map(category => (
                                        <option key={category.categoryId} value={category.categoryId}>
                                            {category.category_name}
                                        </option>
                                    ))}
                                </select>
                            </div>
                        </div>
                        <label>
                            <h4>BRAND</h4>
                        </label>
                        <div className="input-group mb-3 input-box">
                            <input type="text" className="form-control" name="brand"  value={formData.brand} onChange={productChange} required={true}></input>
                        </div>
                        <label>
                            <h4 className="">PRODUCT DESCRIPTION</h4>
                        </label>
                        <div className="input-group mb-3 input-box">
                            <input type="text" className="form-control" name="description" value={formData.description} onChange={productChange} required={true}></input>
                        </div>
                        <label>
                            <h4 className="">AVAILABLE QUANTITY</h4>
                        </label>
                        <div className="input-group mb-3 input-box">
                            <input type="text" className="form-control" name="stock_quantity" value={formData.stock_quantity} onChange={productChange} required={true}></input>
                        </div>
                        <label>
                            <h4 className="">PRICE</h4>
                        </label>
                        <div className="input-group mb-3 input-box">
                            <span className="input-group-text price-icon">$</span>
                            <input type="text" className="form-control" name="price" value={formData.price} onChange={productChange} required={true}></input>
                        </div>
                        <label>
                            <h4>IMAGE</h4>
                        </label>
                        <div className="input-group mb-3 input-box">
                            <input type="file" className="form-control" name="images" value={formData.images} id="formFileSm" onChange={productChange}></input>
                        </div>
                        <button type="submit" className="btn btn-primary">Save Product</button>
                    </form>
                    </td>
                </tr>
            )}
            <ToastContainer />
        </>
    );
};

export default ProductItem;
