import React from 'react';

const CartItem = ({ productName, brand, images, price, quantity }) => {
    return (
        <div className="card-container">
            <div className="card">
                {images && (
                    <img
                        src={images[0]}
                        className="card-img-top card-images"
                        alt={productName}
                    />
                )}
                <div className="card-body desc">
                    <p className="brand-txt">{brand}</p>
                    <h5 className="card-title">{title}</h5>
                    <p className="product-desc">
                        {showFullDescription ? description : description.slice(0, 100)}
                        {description.length > 100 && (
                            <span className="toggle-description" onClick={toggleDescription}>
                              {showFullDescription ? '...less' : '...more'}</span>
                        )}
                    </p>
                    <h4 className="price-txt">${price}</h4>
                    <div className="add-to-cart-container">
                        <button
                            className="add-to-wishlist"
                            onClick={handleSaveToWishlist}>
                            <i className="bi bi-heart-fill heart-icon" id={productId}></i>
                        </button>
                        <button
                            className="add-to-cart"
                            onClick={handleAddToCart}>
                            <i className="bi bi-cart3 cart-icon" id={productId}></i>
                        </button>
                    </div>
                </div>
            </div>
        </div>
    );
};

export default CartItem;
