import React, {useEffect, useState} from 'react';
import "../AdminPage/ProductItem.css";
import {toast} from "react-toastify";

const ProductItem = ({productName, categoryName, price, stockQuantity}) => {
    const [products, setProducts] = useState([]);

    useEffect(() => {
        fetchProducts();
    }, []);

    const fetchProducts = () => {
        fetch("http://localhost:8080/nexusHub/product/update", {
            method: 'PUT',
            headers: {
                'Content-Type': 'application/json'
            },
            body: JSON.stringify(data)
        })
            .then(response => {
                if (response.ok) {
                    return response.json();
                }
                throw new Error('Network response was not ok.');
            }).then(data => {
            console.log("Fetched products:", data);
            setProducts(data); // Update state with fetched products
        })
            .catch(error => {
                console.error('Error:', error);
                toast.error('Failed to fetch products.');
            });
    };
    return (
        <>
            <tr>
                <td className="tr-col">{productName}</td>
                <td className="tr-col">{categoryName}</td>
                <td className="tr-col">{price}</td>
                <td className="tr-col">{stockQuantity}</td>
                <td className="tr-col"><button className="btn btn-primary">UPDATE</button></td>
                <td className="tr-col"><button className="btn btn-danger">DELETE</button></td>
            </tr>
        </>
    );
};

export default ProductItem;
