import './Item.css';
import {useEffect, useState} from "react";


const Item = ({ id, title, images, description, price }) => {
        const [clothesDetail, setClothesDetail] = useState([]);

    useEffect(() => {
        const apiUrl = "https://api.escuelajs.co/api/v1/products"
        const fetchURL = async() =>{
            try {
                const res = await fetch(apiUrl);
                const data = await res.json();
                setClothesDetail(data);
                console.log(data);
            } catch (error) {
                console.error('Error fetching clothes details:', error);
            }
        }
        fetchURL();
    },[]);

    return (
        <div className="card-container">
            {clothesDetail.length > 0 && (
                <div className="card">
                    {clothesDetail.map((item) => (
                        <div key={item.id}>
                            <img src={item.images[0]} className="card-img-top card-images" alt={item.title} />
                            <div className="card-body desc">
                                <p className="brand-txt">NexusHub</p>
                                <h5 className="card-title">{item.title}</h5>
                                <p className="product-desc">{item.description}</p>
                                <h4 className="price-txt">{item.price}</h4>
                                <button className="add-to-cart"><i className="bi bi-cart3 cart-icon" id={item.id}></i></button>
                            </div>
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
};

export default Item;
