import React, {useEffect, useState} from 'react';
import "./ChatBot.css";
import {Link} from "react-router-dom";

export default function ChatBot({ onClose }) {
    const [isOpen, setIsOpen] = useState(true);

    function closeChat() {
        setIsOpen(false);
        onClose();
    }

    useEffect(() => {
        const messageInput = document.getElementById("messageInput");
        const submitButton = document.getElementById("submitButton");

        function handleKeyDown(event) {
            if (event.key === "Enter" && !event.shiftKey) {
                sendMessage();
                event.preventDefault(); // Prevent the default Enter key behavior (e.g., line break)
            } else if (event.key === "Enter" && event.shiftKey) {
                event.preventDefault(); // Prevent the default Enter key behavior
                // Insert a newline character in the textarea
                const currentPos = messageInput.selectionStart;
                const text = messageInput.value;
                const newText = text.substring(0, currentPos) + "\n" + text.substring(currentPos);
                messageInput.value = newText;
            }
        }

        function handleButtonClick() {
            sendMessage();
        }

        function handleInputChange() {
            submitButton.style.visibility = messageInput.value.trim() !== "" ? "visible" : "hidden";
        }

        messageInput.addEventListener("keydown", handleKeyDown);
        submitButton.addEventListener("click", handleButtonClick);
        messageInput.addEventListener("input", handleInputChange);

        return () => {
            messageInput.removeEventListener("keydown", handleKeyDown);
            submitButton.removeEventListener("click", handleButtonClick);
            messageInput.removeEventListener("input", handleInputChange);
        };
    }, []);

    // Add a function to handle sending the message
    function sendMessage() {
        const messageInput = document.getElementById("messageInput");
        const message = messageInput.value.trim();

        function sendMessage() {
            const messageInput = document.getElementById("messageInput");
            const message = messageInput.value.trim();

            if (message !== "") {
                // Append the user message to the chatMessages list
                setChatMessages(prevMessages => [...prevMessages, { text: message, type: 'user' }]);

                // Check if there is a predefined response for the user's input
                const response = getPredefinedResponse(message);

                if (response) {
                    // Append the chatbot response to the chatMessages list with a delay
                    setTimeout(() => {
                        setChatMessages(prevMessages => [...prevMessages, { text: response, type: 'bot' }]);
                    }, 1000); // Replace 1000 milliseconds with your desired delay
                }

                // Reset the input
                messageInput.value = "";
            }
        }

        // Function to check if there is a predefined response for the user's input
        function getPredefinedResponse(userInput) {
            const lowerCaseInput = userInput.toLowerCase();

            for (let i = 0; i < chatResponseObjects.length; i++) {
                const question = chatResponseObjects[i].question;

                if ((typeof question === 'string' && lowerCaseInput === question.toLowerCase()) ||
                    (question instanceof RegExp && question.test(userInput))) {
                    return chatResponseObjects[i].response;
                }
            }

            return null;
        }
    }

    function createLoadingMessage() {
        const loadingMessageElement = document.createElement("li");
        loadingMessageElement.classList.add("message", "loading");
        loadingMessageElement.innerHTML = "<span class='bi bi-three-dots'></span>";
        return loadingMessageElement;
    }

    // Function to check if there is a predefined response for the user's input
    function getPredefinedResponse(userInput) {
        const lowerCaseInput = userInput.toLowerCase();

        for (let i = 0; i < chatResponseObjects.length; i++) {
            const question = chatResponseObjects[i].question;

            if ((typeof question === 'string' && lowerCaseInput === question.toLowerCase()) ||
                (question instanceof RegExp && question.test(userInput))) {
                return chatResponseObjects[i].response;
            }
        }

        return null;
    }

    // Function to get the formatted date
    function getFormattedDate() {
        const currentDate = new Date();
        const options = { weekday: 'long', year: 'numeric', month: 'long', day: 'numeric' };
        return currentDate.toLocaleDateString('en-US', options);
    }

    function getCurrentTime() {
        const currentTime = new Date();
        return currentTime.toLocaleTimeString('en-US');
    }

    const chatResponseObjects = [
        { question: /^(hello|hi|what's up|what's up!|hey)$/i, response: "Hi, how are you?" },
        { question: /^(what is your name\?|who are you\?)$/i, response: "I'm a chatbot, nice to meet you!" },
        { question: /^(what is the date today\?|date|what is the date today|what is date\?|what is date today\?)$/i, response: getFormattedDate() },
        { question: /^(I need help|help|Can you help me\?|Can you help me|Can you help)$/i, response: "How can I help you?"},
        { question: /^(what time is it\?|time|what time is it|what time is it now\?|what time is it now)$/i, response: getCurrentTime() },
        { question: /^(I have technical issue|I have technical problem|technical issue|technical problem)$/i, response: "Please find the link above, and it will direct you to customer service page." },
        { question: /^(I want to return my product|I want to return product|How can I return product\?|How to return product\?|return product|return)$/i, response: "Please find the link above, and it will direct you to customer service page."},
        { question: /^(How long does it take to ship\?|How long does it take for shipping\?|How long does it take for delivery\?)$/i, response: "It usually takes 3-4 business days"},
        { question: /^(Assistant|Representative|Assistant please|representative please|I want to talk to representative|I want to talk to assistant|I want to talk to person)$/i, response: "Please find the link above, and it will direct you to customer service page." },
        { question: /.*/, response: "I'm sorry, I didn't understand that. How can I assist you?" }
    ];

    document.getElementById("messageInput").addEventListener("input", function() {
        const submitButton = document.getElementById("submitButton");
        submitButton.style.visibility = this.value.trim() !== "" ? "visible" : "hidden";
    });
    
    return (
        <div className="chatbot" style={{ display: isOpen ? 'block' : 'none' }}>
            <div className="chatbot-header">
                <button type="button" className="btn-close close-btn" aria-label="Close" onClick={closeChat}></button>
                <span className="chatbot-name">ChatBot</span>
            </div>
            <div className="chatBox">
                <div className="robot-message message">
                    <span id="robot" className="bi bi-robot robot-icon"></span>
                    <div className="initial-message">
                        <p id="chatbotInitialMessage" className="message1">Hi! I am a Chatbot</p>
                        <p className="message1">How can I be of assistance to you?</p>
                    </div>
                </div>
                <div className="quick-link-container">
                    <ul className="quick-link-list" id="chatMessages">
                        <li className="link-item"><i className="bi bi-caret-right"></i><Link to="/tech-support">Technical issue?</Link></li>
                        <li className="link-item"><i className="bi bi-caret-right"></i><Link to="/return">Request return your order</Link></li>
                        <li className="link-item"><i className="bi bi-caret-right"></i><Link to="/tech-support">Check your order status</Link></li>
                    </ul>
                </div>
                <div className="input-container">
                    <input type="text" className="type-section" id="messageInput" placeholder="Type message here" autoComplete="off" autoFocus/>
                        <i id="submitButton" className="bi bi-arrow-right-circle"/>
                </div>
            </div>
        </div>
    )
}
