import React, {useState} from 'react';
import "./ChatBot.css";
import {Link} from "react-router-dom";

export default function ChatBot({ onClose }) {
    const [isOpen, setIsOpen] = useState(true);

    function closeChat() {
        setIsOpen(false);
        onClose();
    }

    return (
        <div className="chatbot" style={{ display: isOpen ? 'block' : 'none' }}>
            <div className="chatbot-header">
                <button type="button" className="btn-close close-btn" aria-label="Close" onClick={closeChat}></button>
                <span className="chatbot-name">ChatBot</span>
            </div>
            <div className="chatBox">
                <div className="robot-message message">
                    <span id="robot" className="bi bi-robot robot-icon"></span>
                    <p id="chatbotInitialMessage" className="initial-message">Hi! I am a Chatbot</p>
                    <p className="initial-message">How can I be of assistance to you?</p>
                </div>
                <div className="quick-link-container">
                    <ul className="quick-link-list" id="chatMessages">
                        <li className="link-item"><i className="bi bi-caret-right-fill"></i><Link to="/tech-support">Technical issue?</Link></li>
                        <li className="link-item"><i className="bi bi-caret-right"></i><Link to="/return">Request return your order</Link></li>
                        <li className="link-item"><i className="bi bi-caret-right"></i><Link to="/tech-support">Check your order status</Link></li>
                    </ul>
                </div>
                <div className="input">
                    <input type="text" id="messageInput" placeholder="Type message here" autoComplete="off" autoFocus/>
                        <i id="submitButton" className="bi bi-arrow-right-circle"/>
                </div>
            </div>
        </div>
    )
}
