import React, {useState} from 'react';
import {Link} from "react-router-dom";
import {toast, ToastContainer} from "react-toastify";
import "./ForgotPage.css";

const ForgotPage = () => {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');

    function findUsername(e) {
        e.preventDefault();

        // Check if any required field is empty
        if (email === '') {
            toast.warn('Please fill out fields', {
                position: "top-center",
                draggable: true,
                hideProgressBar: true
            });
            return;
        }

        const findUser = {username, email};
        console.log(findUser);

        fetch("localhost:8080/chatbot/user/{email}", {
            method: "GET",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(findUser)
        }).then((response) => {
            if (response.ok) {
                console.log("Found User")
                toast.success("Your Username is" + username, {
                    position: "bottom-center",
                    draggable: true,
                    hideProgressBar: true
                });

                setEmail('');
            } else {
                console.error("Email or Phone Number doesn't exist")
                if (response.status === 404) {
                    toast.warn("Email doesn't exists", {
                        position: "bottom-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                } else {
                    toast.error('Could not find username for some reason, please try again later', {
                        position: "bottom-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                }
            }
        })
    }

    return (
        <div className="forgot">
            <form className="forgot-form">
                <div className="forgot-container">
                    <div className="forgot-header">
                        <div className="title-username">
                            <p className="forgot-title text-uppercase fs-3 fw-semibold">Username</p>
                        </div>
                        <div className="title-password">
                            <p className="forgot-title text-uppercase fs-3 fw-semibold">Password</p>
                        </div>
                    </div>

                    <div className="info-container">
                        <label>
                            <p className="m-1 text-uppercase fs-5 fw-semibold text-body-secondary">Email Address:</p>
                            <input className="email" type="email" name="email" placeholder="Email" required={true}
                                   value={email} onChange={(e) => setEmail(e.target.value)}/>
                        </label>
                    </div>
                    <input className="m-4 btn btn-primary fs-5 text-uppercase" id="registerButton" type="submit" value="Find User" onClick={findUsername}/>
                    <div className="register-footer">
                        <span className="m-3 fs-5">Already have an account?</span><Link to="/main" className="fs-5">Login</Link>
                    </div>
                </div>
            </form>
            <ToastContainer />
        </div>
    );
};

export default ForgotPage;
