import React, {useEffect, useState} from 'react';
import './Header.css';
import * as bootstrap from 'bootstrap';
import {Link} from "react-router-dom";
import { ToastContainer, toast } from 'react-toastify';
import 'react-toastify/dist/ReactToastify.css';


export default function Header() {

    const [username, setUsername] = useState('');
    const [password, setPassword] = useState('');
    const [isLoggedIn, setIsLoggedIn] = useState(false);
    const [rememberMe, setRememberMe] = useState(false);

    useEffect(() => {
        // Check if user is logged in by checking localStorage
        const token = localStorage.getItem('token'); // Assuming you store token upon successful login
        setIsLoggedIn(!!token);
    }, []);

    // Load remembered username from localStorage
    useState(() => {
        const rememberedUsername = localStorage.getItem('rememberedUsername');
        if (rememberedUsername) {
            setUsername(rememberedUsername);
            setRememberMe(true);
        }
    }, []);

    // Save username to localStorage if "Remember Me" is checked
    if (rememberMe) {
        localStorage.setItem('rememberedUsername', username);
    } else {
        localStorage.removeItem('rememberedUsername');
    }
    
    function handleSignIn(e) {
        e.preventDefault();
        const user = {username, password}
        console.log(user);
        fetch("http://localhost:8080/chatbot/authenticate", {
            method: "POST",
            headers: {"Content-Type": "application/json"},
            body: JSON.stringify(user)
        }).then((response) => {
            if (response.ok) {
                console.log('Login Successful');
                response.json().then(data => {
                    localStorage.setItem('token', data.token);
                    setIsLoggedIn(true);
                    toast.success('Login Successful', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                })

                // Schedule token removal after 1 hour
                setTimeout(() => {
                    localStorage.removeItem('token');
                    setIsLoggedIn(false);
                    toast.warn('Session Expired. Please login again.', {
                        position: "top-center",
                        draggable: true,
                        hideProgressBar: true
                    });
                }, 60000 * 60);

                setUsername('');
                setPassword('');
            } else {
                console.error('Login Failed');
                toast.warn('Username or Password is incorrect', {
                    position: "top-center",
                    draggable: true,
                    hideProgressBar: true
                    }
                    );
            }
        })
            .catch((error) => {
                console.error('Error:', error);
            });
    }

    function handleLogOut() {
        // Clear token from localStorage upon logout
        localStorage.removeItem('token');

        setIsLoggedIn(false);
        toast.success("Logout Successful", {
            position: "top-center",
            draggable: true,
            hideProgressBar: true
            }
            )
    }


    return(
        <div className="header">
            <div className="logo-container">
                <Link to="/main" className="logo-link">
                    <h2 className="logo-txt">NexusHub</h2>
                </Link>
            </div>
            <div className="nav-container">
                <ul className="nav nav-pills nav-list">
                    <li className="nav-item">
                        <div className="dropdown">
                            {isLoggedIn ? (<button type="button"
                                                   className="btn logout-btn"
                                                   onClick={handleLogOut}>
                                Sign Out
                            </button>
                            ) : (<button type="button"
                                                  className="btn dropdown-toggle sign-btn"
                                                  data-bs-toggle="dropdown" aria-expanded="false"
                                                  data-bs-auto-close="outside">
                                SIGN IN
                            </button>)}


                            <form className={`dropdown-menu p-4 sign-form ${
                                isLoggedIn ? 'd-none' : ''
                            }`}>
                                <div className="mb-3">
                                    <label htmlFor="exampleDropdownFormUsername" className="form-label">User Name</label>
                                    <input type="text" className="form-control" id="exampleDropdownFormUsername" placeholder="User Name"
                                           value={username} onChange={(e) => setUsername(e.target.value)}/>
                                </div>
                                <div className="mb-3">
                                    <label htmlFor="exampleDropdownFormPassword2" className="form-label">Password</label>
                                    <input type="password" className="form-control" id="exampleDropdownFormPassword2" placeholder="Password"
                                            value={password} onChange={(e) => setPassword(e.target.value)}/>
                                </div>
                                <div className="mb-3">
                                    <div className="form-check">
                                        <input type="checkbox" className="form-check-input" id="dropdownCheck2"
                                               checked={rememberMe}
                                               onChange={(e) => setRememberMe(e.target.checked)}/>
                                            <label className="form-check-label" htmlFor="dropdownCheck2">
                                                Remember me
                                            </label>
                                    </div>
                                </div>
                                <button type="submit" className="btn btn-primary" onClick={handleSignIn}>Sign in</button>
                                <Link to="/register" className="register-link"><span className="register-txt">Doesn't have account yet?</span></Link>
                            </form>
                        </div>
                    </li>
                    <li className="nav-item dropdown">
                        <a className="btn dropdown-toggle shop-btn" data-bs-toggle="dropdown" data-bs-target="navBar" href="#" role="button" aria-expanded="false">SHOP</a>
                        <ul className="dropdown-menu shop-list-container" id="navBar">
                            <li><a className="dropdown-item shop-list" href="#">Fashion & Clothes</a></li>
                            <li><a className="dropdown-item shop-list" href="#">Sports</a></li>
                            <li><a className="dropdown-item shop-list" href="#">Electronics</a></li>
                            <li><a className="dropdown-item shop-list" href="#">Home & Garden</a></li>
                        </ul>
                    </li>
                    <li className="nav-item">
                        <a className="nav-link" href="#"><i className="bi bi-bag-check bag-img"></i></a>
                    </li>
                </ul>
            </div>
            <ToastContainer />
        </div>
    )
}
