import './Item.css';
import {useEffect, useState} from "react";

const apiUrl = "https://api.escuelajs.co/api/v1/products/?categoryId=1"
const Item = ({ imageURL, title, rating, price }) => {
        const [clothesDetail, setClothesDetail] = useState(null);

    useEffect(() => {
        const fetchClothesCategory = async() =>{
            try {
                const res = await fetch(apiUrl);
                if (!res.ok) {
                    throw new Error('Failed to fetch data');
                }
                const data = await res.json();
                setClothesDetail(data);
                console.log(data);
            } catch (error) {
                console.error('Error fetching clothes details:', error);
            }
        }
    })



    return (
        <div className="card-container">
            <div className="card">
                <img src={imageURL} className="card-img-top card-images" alt={title} />
                <div className="card-body desc">
                    <p className="brand-txt">NexusHub</p>
                    <h5 className="card-title">{title}</h5>
                    <div className="rating">
                        {[...Array(rating)].map((_, index) => (
                            <i className="bi bi-star-fill"></i>
                        ))}
                    </div>
                    <h4 className="price-txt">{price}</h4>
                    <button className="add-to-cart"><i className="bi bi-cart3 cart-icon"></i></button>
                </div>
            </div>
        </div>
    );
};

export default Item;
