import React, {useState} from 'react';
import "./ChatBot.css";

export default function ChatBot({ onClose }) {
    const [isOpen, setIsOpen] = useState(true);

    function closeChat() {
        setIsOpen(false);
        onClose();
    }

    return (
        <div className="chatbot" style={{ display: isOpen ? 'block' : 'none' }}>
            <div className="chatbot-header">
                <button type="button" className="btn-close close-btn" aria-label="Close" onClick={closeChat}></button>
                <span>ChatBot</span>
            </div>
            <div className="chatBox">
                <div className="inital message chatbot">
                    <i id="robot" className="bi bi-robot"></i>
                    <p id="chatbotInitialMessage">Hi! I am a Chatbot How can I be of assistance to you?</p>
                </div>
                <ul className="chatMessages" id="chatMessages">
                    <li className="link">
                        <a href="#" target="_blank">Technical issue?</a>
                        <a href="#" id="returnOrderLink" target="_blank">Request return your order</a>
                        <a href="#" id="checkOrderStatusLink" target="_blank">Check your order status</a>
                    </li>
                </ul>
                <div className="input">
                    <input type="text" id="messageInput" placeholder="Type message here" autoComplete="off" autoFocus/>
                        <i id="submitButton" className="bi bi-arrow-right-circle"/>
                </div>
            </div>
        </div>
    )
}
