import React, {useEffect, useState} from 'react';
import Header from "../Header";
import Item from "./Item";
import FashionPage from "./FashionPage";

const ElectronicsPage = ({ handleSignIn, handleLogOut, handleMoreClick }) => {
    const [fashionItem, setFashionItem] = useState([]);
    const [itemToShow, setItemToShow] = useState(10);
    const [isLoading, setIsLoading] = useState(true);

    useEffect(() => {
        const apiUrl = "https://api.escuelajs.co/api/v1/products/?categoryId=2";
        const fetchFashionItems = async () => {
            try {
                const response = await fetch(apiUrl);
                if (!response.ok) {
                    throw new Error('Failed to fetch fashion items');
                }
                const data = await response.json();
                setIsLoading(false);
                setFashionItem(data);
                setItemToShow(8);
            } catch (error) {
                console.error('Error fetching fashion items:', error);
            }
        };

        fetchFashionItems();
    }, []);

    // function handleMoreClick() {
    //     setItemToShow((prev) => prev + 2);
    //     const loadBtn = document.querySelector(".loadbtn");
    //     loadBtn.style.display = "none";
    // }

    return (
        <>
            <Header handleSignIn={handleSignIn} handleLogOut={handleLogOut}/>
            <FashionPage handleMoreClick={handleMoreClick}></FashionPage>
            
        </>
    );
};

export default ElectronicsPage;
